# -*- coding: utf-8 -*-from django import formsfrom django.utils.translation import ugettext as _from articles.models import Category, Articleclass ArticleForm(forms.Form):	"""Submitting article form"""	title = forms.CharField(max_length=200)	body = forms.CharField(widget=forms.Textarea)	category = forms.ChoiceField(choices=Category.objects.choices(), required=False)	tags = forms.CharField(max_length=100, required=False)	orig_link = forms.URLField()	image = forms.ImageField(required=False)		def create_article(self, user):		article = Article()		article.update(user, self.cleaned_data)		return article			def save(self, user):		article = self.create_article(user)		article.save()		return articleclass ArticlePreviewForm(forms.Form):	"""Submitting article form"""	title = forms.CharField(widget=forms.HiddenInput)	body = forms.CharField(widget=forms.HiddenInput)	category = forms.CharField(widget=forms.HiddenInput, required=False)	tags = forms.CharField(widget=forms.HiddenInput, required=False)	orig_link = forms.CharField(widget=forms.HiddenInput)	image = forms.CharField(widget=forms.HiddenInput, required=False)